replacements = File.read('input')
  .lines
  .map { |line| line.chomp.split(' => ') }

starting = ""
molecules = []

replacements.each do |from, to|
  starting.chars.each.with_index do |_, i|
    if starting[i..(i + from.size - 1)] == from
      transformation = starting[..(i-1)] + to + starting[(i+from.size)..]
      molecules << transformation
    end
  end
end

p molecules.uniq.size

